import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor (props) {
      super(props);
      this.state = {
        romanNumber: '',
        romanResponse: '',
        numericNumber: '',
        numericResponse: '',
      }
      this.romanChange = this.romanChange.bind(this);
      this.numericChange = this.numericChange.bind(this);

      this.handleToNumeric = this.handleToNumeric.bind(this);      
      this.handleToRoman = this.handleToRoman.bind(this);
  }

  romanChange (event) {
    this.setState({
        romanNumber: event.target.value
    })
  }

  numericChange (event) {
    this.setState({
      numericNumber: event.target.value
    })
  }

  handleToNumeric = async (event) =>{    
    event.preventDefault();
    const response = await fetch(`/api/v1/convertToNumeric?number=${this.state.romanNumber}`);
    try{
      const body = await response.json();
      this.setState({ romanResponse: body.message })  
    } catch(error){
      throw Error(error) 
    }       
  } 

  handleToRoman = async (event) =>{    
    event.preventDefault();
    const response = await fetch(`/api/v1/convertToRoman?number=${this.state.numericNumber}`);
    try{
      const body = await response.json();
      this.setState({ numericResponse: body.message })  
    } catch(error){
      throw Error(error) 
    }       
  } 

  render() {
    return (
      <div className="App" >
          <form onSubmit={this.handleToNumeric}>
              <label>Roman to Numeric</label>
              <input type="text" value={this.state.romanNumber} onChange={this.romanChange}/>
              <input type="submit" value="Submit"/>
          </form>
          <textarea value={this.state.romanResponse}></textarea>
          <form onSubmit={this.handleToRoman}>
              <label>Numeric to Roman</label>
              <input type="text" value={this.state.numericNumber} onChange={this.numericChange}/>
              <input type="submit" value="Submit"/>
          </form>
          <textarea value={this.state.numericResponse}></textarea>
      </div>       
    );
  }
}
export default App;
