import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor (props) {
      super(props);
      this.state = {
          inputvalue: '',
          response: ''
      }
      this.romanChange = this.romanChange.bind(this);
      this.handleToNumeric = this.handleToNumeric.bind(this);
      this.numericChange = this.numericChange.bind(this);
      this.handleToRoman = this.handleToRoman.bind(this);
  }

  handleChange (event) {
    this.setState({
        inputvalue: event.target.value
    })
  }
  handleSubmit = async (event) =>{    
    event.preventDefault();
    const response = await fetch(`/api/v1/convertToNumeric?number=${this.state.inputvalue}`);
    try{
      const body = await response.json();
      this.setState({ response: body.message })  
    } catch(error){
      throw Error(error) 
    }       
}

  render() {
    return (
      <div className="App" onSubmit={this.handleSubmit}>
          <form>
              <label>Name</label>
              <input type="text" value={this.state.inputvalue} onChange={this.handleChange}/>
              <input type="submit" value="Submit"/>
          </form>
          <textarea value={this.state.response}></textarea>
      </div>      
    );
  }
}
export default App;
